import React, { Component } from 'react'
import SearchList from './SearchList'

class SearchForm extends Component {
    constructor(props){
        super(props);
        this.state= {
            isSearch: false,
            apiURL: "https://api.giphy.com/v1/gifs/search?",
            apiKey: "api_key=wuG901dsm94iHrP02PJ1NVDZvzoNXdfB",
            lang: "tr",
            limit: 25,
            offset: 0,
            rating: "G",
            query: ""
        };

        this.submitForm= this.submitForm.bind(this);
        this.queryChange = this.queryChange.bind(this);
    }

    queryChange(event){
        this.setState({
            query: event.target.value
        });
    }
    submitForm(event){
        const {query, apiURL, apiKey} = this.state;
        let createAPIURL = apiURL + apiKey + "&q=" + query;
        //alert("Form submit edildi | " + createAPIURL);
    }

    render() {
        return (
            <div>
                <div className="row">
                    <form style={{width: "100%"}} onSubmit={this.submitForm}>
                        <div className="col-12">
                            <div className="mb-3">
                                <input type="text" className="form-control" id="firstName" placeholder="Birşeyler yazın..." value={this.state.query} onChange={this.queryChange} />
                            </div>
                            <div className="mb-3">
                                <input  className="btn btn-primary btn-lg btn-block" type="submit" value="Gif Ara" />
                            </div>
                        </div>
                    </form>
                </div>
                <SearchList myApiURL={createAPIURL} />
            </div>
        )
    }
}

export default SearchForm;
