import React, { Component } from 'react'
import SearchList from './SearchList'

class SearchForm extends Component {
    constructor(props){
        super(props);
        this.state= {
            isSearch: false,
            apiURL: "https://api.giphy.com/v1/gifs/search?",
            apiKey: "api_key=wuG901dsm94iHrP02PJ1NVDZvzoNXdfB",
            lang: "tr",
            limit: 25,
            offset: 0,
            rating: "G",
            query: "",
            createAPIURL: ""
        };

        this.searchForm= this.searchForm.bind(this);
        this.queryChange = this.queryChange.bind(this);
    }

    queryChange(event){
        this.setState({
            query: event.target.value
        });
    }

    searchForm(event){
        const {query, apiURL, apiKey} = this.state;
        this.setState({
            createAPIURL: apiURL + apiKey + "&q=" + query,
            isSearch: true
        });
    }

    render() {
        const {createAPIURL, isSearch} = this.state;

        fetch("https://api.giphy.com/v1/gifs/search?api_key=wuG901dsm94iHrP02PJ1NVDZvzoNXdfB&q=trump&limit=25&offset=0&rating=G&lang=en").then(response => {
            console.log(response);
        });

        return (
            <div>
                <div className="row">
                    <form style={{width: "100%"}}>
                        <div className="col-12">
                            <div className="mb-3">
                                <input type="text" className="form-control" id="firstName" placeholder="Birşeyler yazın..." value={this.state.query} onChange={this.queryChange} />
                            </div>
                            <div className="mb-3">
                                <button  className="btn btn-primary btn-lg btn-block" type="button" onClick={this.searchForm}> Gif Ara </button>
                            </div>
                        </div>
                    </form>
                </div>
                <SearchList myApiURL={createAPIURL} isSearch={isSearch} />
            </div>
        )
    }
}

export default SearchForm;
